$('.instagram-feed').socialstream({
    socialnetwork: 'instagram',
    limit: 9,
    username: 'templateszoo'
})
$('.flickr-feed').socialstream({
    socialnetwork: 'flickr',
    limit: 3,
    username: 'Mrky1'
})
